import axios from 'axios';

const API_URL = 'http://localhost:5000/api/graph';

export const fetchGraph = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching graph:', error);
    return null;
  }
};

export const saveGraph = async (graph) => {
  try {
    const response = await axios.post(API_URL, graph);
    return response.data;
  } catch (error) {
    console.error('Error saving graph:', error);
    return null;
  }
};

export const updateGraph = async (id, graph) => {
  try {
    const response = await axios.put(`${API_URL}/${id}`, graph);
    return response.data;
  } catch (error) {
    console.error('Error updating graph:', error);
    return null;
  }
};
