// File: client/src/components/Node.jsx

import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { deleteNode } from "../redux/graphSlice";
import "./Node.css"; // CSS for Node component

const Node = ({ node, onDragStart, onDelete, onAddEdge }) => {
  const dispatch = useDispatch();

  const [showMenu, setShowMenu] = useState(false);

  const handleDeleteNode = () => {
    dispatch(deleteNode(node.id));
    onDelete();
  };

  const handleEdgeAdd = (e) => {
    // Start edge creation process
    onAddEdge(node.id);
  };

  return (
    <div
      className="node"
      style={{ left: node.position.x, top: node.position.y }}
      onMouseDown={onDragStart}
      onContextMenu={(e) => {
        e.preventDefault();
        setShowMenu(true);
      }}
    >
      <div className="node-content">
        <span>{node.id}</span>
      </div>

      {/* Context menu for node actions */}
      {showMenu && (
        <div className="node-menu">
          <button onClick={handleDeleteNode}>Delete Node</button>
          <button onClick={handleEdgeAdd}>Add Edge</button>
        </div>
      )}
    </div>
  );
};

export default Node;
