import React from 'react';

const Node = ({ id, label, position, onClick, isSelected, onDelete }) => {
  return (
    <div
      id={id}
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '50px',
        height: '50px',
        backgroundColor: isSelected ? 'lightgreen' : 'lightblue',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: '2px solid black',
        cursor: 'pointer',
      }}
      onClick={onClick}
    >
      {label}
      <button
        style={{
          position: 'absolute',
          top: '-10px',
          right: '-10px',
          background: 'red',
          color: 'white',
          borderRadius: '50%',
          border: 'none',
          cursor: 'pointer',
        }}
        onClick={(e) => {
          e.stopPropagation();
          onDelete();
        }}
      >
        X
      </button>
    </div>
  );
};

export default Node;
