import React, { useRef } from 'react';
import { useDispatch } from 'react-redux';
import { updateNodePosition } from '../redux/graphSlice';

const Node = ({ id, label, position, onClick, isSelected, onDelete }) => {
  const dispatch = useDispatch();
  const nodeRef = useRef(null);

  const handleDragStart = (e) => {
    e.dataTransfer.setDragImage(new Image(), 0, 0); // Prevent ghost drag image
  };

  const handleDrag = (e) => {
    if (e.clientX === 0 && e.clientY === 0) return; // Ignore ghost drag events

    // Update position in Redux
    const newX = e.clientX - nodeRef.current.offsetWidth / 2;
    const newY = e.clientY - nodeRef.current.offsetHeight / 2;
    dispatch(updateNodePosition({ id, position: { x: newX, y: newY } }));
  };

  return (
    <div
      id={id}
      ref={nodeRef}
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '60px',
        height: '60px',
        backgroundColor: isSelected ? '#90ee90' : '#87ceeb', // Highlight color
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: '2px solid black',
        cursor: 'grab',
        fontSize: '14px',
        color: 'black',
        fontWeight: 'bold',
      }}
      draggable
      onDragStart={handleDragStart}
      onDrag={handleDrag}
      onClick={onClick}
    >
      {label}
      <button
        style={{
          position: 'absolute',
          top: '4px',
          right: '4px',
          background: 'red',
          color: 'white',
          borderRadius: '50%',
          border: 'none',
          width: '16px',
          height: '16px',
          cursor: 'pointer',
          padding: 0,
          fontSize: '10px',
        }}
        onClick={(e) => {
          e.stopPropagation();
          onDelete();
        }}
      >
        X
      </button>
    </div>
  );
};

export default Node;
