import React from 'react';
import { useDispatch } from 'react-redux';
import { removeNode } from '../redux/graphSlice';

const Node = ({ id, label, position }) => {
  const dispatch = useDispatch();

  const handleRemove = () => {
    dispatch(removeNode(id));
  };

  return (
    <div
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '100px',
        height: '50px',
        backgroundColor: 'lightblue',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: '1px solid black',
        borderRadius: '8px',
        cursor: 'pointer',
      }}
      draggable
    >
      <span>{label}</span>
      <button
        onClick={handleRemove}
        style={{
          position: 'absolute',
          top: '-10px',
          right: '-10px',
          background: 'red',
          color: 'white',
          borderRadius: '50%',
          border: 'none',
          cursor: 'pointer',
        }}
      >
        X
      </button>
    </div>
  );
};

export default Node;
