import React from 'react';

const Node = ({ id, label, position, onClick, isSelected, onDelete }) => {
  const handleDragStart = (e) => {
    e.dataTransfer.setData('nodeId', id);
  };

  const handleDrag = (e) => {
    if (e.clientX === 0 && e.clientY === 0) return; // Ignore ghost drag events
    const draggedNode = document.getElementById(id);
    if (draggedNode) {
      draggedNode.style.left = `${e.clientX - 25}px`; // Adjust for half the node size
      draggedNode.style.top = `${e.clientY - 25}px`;
    }
  };

  return (
    <div
      id={id}
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '60px',
        height: '60px',
        backgroundColor: isSelected ? '#90ee90' : '#87ceeb', // Highlight color
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: '2px solid black',
        cursor: 'grab',
        fontSize: '14px',
        color: 'black',
        fontWeight: 'bold',
      }}
      draggable
      onDragStart={handleDragStart}
      onDrag={handleDrag}
      onClick={onClick}
    >
      {label}
      <button
        style={{
          position: 'absolute',
          top: '-8px',
          right: '-8px',
          background: 'red',
          color: 'white',
          borderRadius: '50%',
          border: 'none',
          width: '16px',
          height: '16px',
          cursor: 'pointer',
        }}
        onClick={(e) => {
          e.stopPropagation();
          onDelete();
        }}
      >
        X
      </button>
    </div>
  );
};

export default Node;
