import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchGraph, saveGraph } from '../utils/api';
import {
  setGraph,
  addNode,
  removeNode,
  addEdge,
  removeEdge,
  undo,
  redo,
} from '../redux/graphSlice';
import Node from './Node';
import Edge from './Edge';

const GraphEditor = () => {
  const dispatch = useDispatch();
  const { nodes, edges } = useSelector((state) => state.graph);

  useEffect(() => {
    const loadGraph = async () => {
      const graph = await fetchGraph('default'); // Replace with actual graph ID
      if (graph) dispatch(setGraph(graph));
    };
    loadGraph();
  }, [dispatch]);

  const handleSave = async () => {
    const graph = { nodes, edges };
    await saveGraph(graph);
    alert('Graph saved successfully!');
  };

  const handleAddNode = () => {
    dispatch(addNode({ position: { x: Math.random() * 500, y: Math.random() * 500 } }));
  };

  return (
    <div>
      <h1>Graph Editor</h1>
      <div style={{ position: 'relative', width: '100%', height: '500px', border: '1px solid black' }}>
        {edges.map((edge) => (
          <Edge key={`${edge.source}-${edge.target}`} source={edge.source} target={edge.target} />
        ))}
        {nodes.map((node) => (
          <Node key={node.id} id={node.id} label={node.label} position={node.position} />
        ))}
      </div>
      <button onClick={handleAddNode}>Add Node</button>
      <button onClick={handleSave}>Save Graph</button>
      <button onClick={() => dispatch(undo())}>Undo</button>
      <button onClick={() => dispatch(redo())}>Redo</button>
    </div>
  );
};

export default GraphEditor;
